code.installcode:
// Set install path
$corepath = DIR . '/dbtech/dbseo';

if (!file_exists($corepath . '/includes/class_install.php'))
{
	// Installer file missing
	print_dots_stop();
	print_cp_message($corepath . '/includes/class_install.php appears to be missing!');
}

// Require the installer
require_once($corepath . '/includes/class_install.php');

// Initialise the installer
DBTech_Install::init($corepath . '/install', 'dbtech_dbseo');

code.uninstallcode:
// Set install path
$corepath = DIR . '/dbtech/dbseo';

if (!file_exists($corepath . '/includes/class_install.php'))
{
	// Installer file missing
	print_dots_stop();
	print_cp_message($corepath . '/includes/class_install.php appears to be missing!');
}

// Require the installer
require_once($corepath . '/includes/class_install.php');

// Initialise the installer
DBTech_Install::init($corepath . '/install', 'dbtech_dbseo');

// Begin uninstall
DBTech_Install::uninstall();
